import { useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios"; // Add axios for making HTTP requests

export const OrderSuccessCard = ({ paymentId }) => {
  function getRandomInt(max) {
    return Math.floor(Math.random() * max);
  }

  // Function to send SMS confirmation
  const sendSMSConfirmation = async (orderID) => {
    try {
      const response = await axios.post(
        "YOUR_TWILIO_API_ENDPOINT", // Replace with your Twilio API endpoint
        {
          to: "RECIPIENT_PHONE_NUMBER", // Replace with the recipient's phone number
          body: `Your order (${orderID}) has been placed successfully. Thank you!`, // Customize the message as needed
        },
        {
          headers: {
            Authorization: `Bearer YOUR_TWILIO_AUTH_TOKEN`, // Replace with your Twilio Auth Token
          },
        }
      );
      console.log(response.data);
    } catch (error) {
      console.error("Error sending SMS:", error);
    }
  };

  useEffect(() => {
    // Generate a random order ID
    const orderID = "OD" + getRandomInt(9999999999);

    // Send SMS confirmation
    sendSMSConfirmation(orderID);
  }, []); // Empty dependency array to ensure the effect runs only once

  return (
    <>
      {/* Your existing JSX code */}
      <div className="order-box flex-column">
        {/* ... (existing code) */}

        <h3 className="text-center payment-text">Order ID: {"OD" + getRandomInt(9999999999)}</h3>

        {/* ... (existing code) */}

        <Link to="/products" className="btn btn-solid btn-continue-shopping">
          Continue Shopping <i className="material-icons mg-left-xsm">shopping_basket</i>
        </Link>
      </div>
    </>
  );
};
