import { Server, Model, RestSerializer } from "miragejs";
import { loginHandler, signupHandler } from "./backend/controllers/AuthController";
import { addItemToCartHandler, getCartItemsHandler, removeItemFromCartHandler, updateCartItemHandler } from "./backend/controllers/CartController";
import { getAllCategoriesHandler, getCategoryHandler } from "./backend/controllers/CategoryController";
import { getAllProductsHandler, getProductHandler } from "./backend/controllers/ProductController";
import { addItemToWishlistHandler, getWishlistItemsHandler, removeItemFromWishlistHandler } from "./backend/controllers/WishlistController";
import { categories } from "./backend/db/categories";
import { products } from "./backend/db/products";
import { users } from "./backend/db/users";
import { Response } from "miragejs";
import { v4 as uuid } from "uuid";
import { formatDate } from "../backend/utils/authUtils";
const sign = require("jwt-encode");

// Hardcoded JWT secret key for testing purposes
const jwtSecretKey = "/Q5/K8CrI0kz25G/wZ3mquojG/Kf3xIghztXXLdwuZw=";

export function makeServer({ environment = "development" } = {}) {
  return new Server({
    serializers: {
      application: RestSerializer,
    },
    environment,
    models: {
      product: Model,
      category: Model,
      user: Model,
      cart: Model,
      wishlist: Model,
    },

    // Runs on the start of the server
    seeds(server) {
      // Disabling console logs from Mirage
      server.logging = true;
      console.log("Users to be seeded:", users);

      // Seed products
      products.forEach((item) => server.create("product", { ...item }));

      // Seed users with an empty cart and wishlist
      users.forEach((item) => server.create("user", { ...item, cart: [], wishlist: [] }));

      // Seed categories
      categories.forEach((item) => server.create("category", { ...item }));
    },

    routes() {
      this.namespace = "api";

      // Auth routes (public)
      this.post("/auth/signup", signupHandler.bind(this));
      this.post("/auth/login", loginHandler.bind(this));

      // Products routes (public)
      this.get("/products", getAllProductsHandler.bind(this));
      this.get("/products/:productId", getProductHandler.bind(this));

      // Categories routes (public)
      this.get("/categories", getAllCategoriesHandler.bind(this));
      this.get("/categories/:categoryId", getCategoryHandler.bind(this));

      // Cart routes (private)
      this.get("/user/cart", getCartItemsHandler.bind(this));
      this.post("/user/cart", addItemToCartHandler.bind(this));
      this.post("/user/cart/:productId", updateCartItemHandler.bind(this));
      this.delete("/user/cart/:productId", removeItemFromCartHandler.bind(this));

      // Wishlist routes (private)
      this.get("/user/wishlist", getWishlistItemsHandler.bind(this));
      this.post("/user/wishlist", addItemToWishlistHandler.bind(this));
      this.delete("/user/wishlist/:productId", removeItemFromWishlistHandler.bind(this));

      // Middleware to add JWT secret key to the context
      this.pretender.handledRequest = function (verb, path, request) {
        return {
          status: 200,
          headers: { "content-type": "application/json" },
          text: JSON.stringify({ jwtSecretKey }),
        };
      };
    },
  });
}
